<?php

namespace ABC\Core;


use ABC\ABC;
use ABC\Core\Exception\AbcError;
use Abcsoft\TPL\Template\Tpl;
use Abcsoft\TPL\TplNative\TplNative;

/** 
 * Класс Base
 * 
 * NOTE: Requires PHP version 5.5 or later   
 * @author phpforum.su
 * @copyright © 2015
 * @license http://www.wtfpl.net/ 
 */   
class Base
{    
    public $view; 
    protected $tpl;
    protected $layout;
    protected $blockContent; 
    protected $noTpl = false;
    
    /**
    * Ошибка вызова метода
    *
    * @param string $method
    * @param mix $param
    *
    * @return void
    */     
    public function __call($method, $param)
    {
        $method = explode('::', $method);
        AbcError::badMethodCall(array_pop($method) .'() '. ABC_NO_METHOD);
    }  

    /**
    * 
    *
    * @param string $method
    * @param mix $param
    *
    * @return void
    */     
    public function __get($name)
    {
        switch(strtolower($name)) {
            case 'params' :
                return ABC::sharedService(ABC::PARAMS);    
            default :
                AbcError::invalidArgument(sprintf(ABC_PROPERTY_NOT_FOUND, $name, $name));            
        }
    }      
    /**
    * Выбор шаблонизатора и установка шаблона
    *
    * @param string $template
    *
    * @return void
    */     
    public function selectTpl($template, $tpl = true) 
    {
        if(true  === $this->noTpl){
            AbcError::Logic(ABC_SELECT_NO_TEMPLATE);
        }
       
        $config = ABC::getConfig('template');
        $type = $this->getTypeTpl($config);
        $this->tpl = $type === ABC::TEMPLATE ? new Tpl($config) : new TplNative($config);
        $this->tpl->selectTpl($template, $tpl);
    }     
    
    /**
    * Выбор макета
    *
    * @param string $layout
    *
    * @return void
    */     
    public function selectLayout($layout)
    {
        $this->layout = $layout;
    }
    
    /**
    * Передача данных в макет
    *
    * @param string $layout
    *
    * @return void
    */     
    public function assign($data, $value = null)
    {
        if(false === $this->noTpl){
            $this->selectTpl($this->blockContent, false); 
            $this->noTpl = true;
        }
     
        $this->tpl->assign($data, $value);        
    }

    /**
    * Выбор блока для вставки контента
    *
    * @param string $blockContent
    *
    * @return void
    */     
    public function selectBlockContent($blockContent)
    {
        $this->blockContent = $blockContent;
    }
    
    /**
    * Вывод в поток
    *
    * @return void
    */     
    public function render($content = null)
    {
        if (empty($content) && is_object($this->tpl)) {
            
            if (!empty($this->blockContent) && !empty($this->layout)) {
                $this->tpl->extendsTpl($this->blockContent, $this->layout);
            }
            
            $content = $this->tpl->getContent();
            $params = ABC::sharedService(ABC::PARAMS);
            $params->setBody($content);
        }
    }    
    
    /**   
    * Перенаправление
    *
    * @param string $path
    *
    * @return void
    */ 
    public function reDirect($path = null)
    { 
        if(!empty($path)) {
            $this->params->setHeader('Location', createUri($path));
        } else {
            $this->params->setHeader('Location', str_replace("/index.php", "", $_SERVER['HTTP_REFERER']));
        }
        $this->params->sendHeaders(true);
    } 
    
    /**
    * Возвращает объект модели
    *
    * @param string $modelName
    *
    * @return object
    */ 
    protected function getModel($modelName)
    {
        $settings  = ABC::getConfig('settings');
        $space = '\ABC\\'. $settings['application'] .'\\'. $settings['dir_models'] .'\\';
        $model = $space . $modelName;
      
        if (class_exists($model)) {
            return new $model;       
        } 
     
        AbcError::badMethodCall('<strong>'. $model .'</strong> '. ABC_NO_MODEL);
    }
    
    /**
    * Возвращает тип шаблонизатора
    *
    * @return string
    */        
    protected function getTypeTpl($config)
    { 
        if (!isset($config['abc_template']) || true === $config['abc_template']) {
            $type = ABC::TEMPLATE;
        } elseif (false === $type) {
            $type = ABC::TPL_NATIVE;
        } else {
            AbcError::badFunctionCall(ABC_INVALID_DEBUG_SETTING);
        }
        
        return $type;
    } 
    
    /**
    * Генерирует 404 Not Found
    *
    * @param string $search
    *
    * @return void
    */    
    public function action404($search = null)
    {
        $errors = ABC::getConfig('errors');
        
        if (empty($errors['abc_404'])) {
            AbcError::domain('404 <b>'. $search .'</b> not found ');
            return;
        }
        
        header("HTTP/1.1 404 Not Found");
        
        if (file_exists($errors['abc_404'])) {
            $page = file_get_contents($errors['abc_404']);
        } else {
            $page = <<<EOD
<!DOCTYPE html>
<html>
  <head>
    <meta http-equiv="Content-type" content="text/html; charset=utf-8">
    <meta content="origin" name="referrer">
    <title>Page not found</title>
   </head>
   <body>
        <div style="text-align:center">
            <img src="data:image/png;base64," border="0" />
        </div>
    </body>
</html>
EOD;
        }
        
        exit($page);
    }
}
